//==============================================================================
struct QuickJSContext  : public Context::Pimpl
{
    QuickJSContext()
    {
        createContext();
        functionArgs.reserve (32);
    }

    ~QuickJSContext() override
    {
        freeContext();
    }

    void freeContext()
    {
        if (context) { JS_FreeContext (context); context = {}; }
        if (runtime) { JS_FreeRuntime (runtime); runtime = {}; }
    }

    void createContext()
    {
        runtime = JS_NewRuntime();
        context = JS_NewContext (runtime);
        CHOC_ASSERT (context != nullptr);
        JS_SetContextOpaque (context, this);
    }

    void pumpMessageLoop() override {}

    void pushObjectOrArray (const choc::value::ValueView& v) override { functionArgs.push_back (valueToJS (v).release()); }
    void pushArg (std::string_view v) override                        { functionArgs.push_back (stringToJS (v).release()); }
    void pushArg (int32_t v) override                                 { functionArgs.push_back (JS_NewInt32   (context, v)); }
    void pushArg (int64_t v) override                                 { functionArgs.push_back (JS_NewInt64   (context, v)); }
    void pushArg (uint32_t v) override                                { functionArgs.push_back (JS_NewUint32  (context, v)); }
    void pushArg (double v) override                                  { functionArgs.push_back (JS_NewFloat64 (context, v)); }
    void pushArg (bool v) override                                    { functionArgs.push_back (JS_NewBool    (context, v)); }

    choc::value::Value evaluateExpression (const std::string& code) override
    {
        return takeValue (JS_Eval (context, code.c_str(), code.size(), "", JS_EVAL_TYPE_GLOBAL)).toChocValue();
    }

    void run (const std::string& code, Context::ReadModuleContentFn* resolveModule, Context::CompletionHandler handleResult) override
    {
        try
        {
            if (resolveModule != nullptr)
            {
                JS_SetModuleLoaderFunc (runtime, nullptr, moduleLoaderFunc, resolveModule);
                auto result = takeValue (JS_Eval (context, code.c_str(), code.size(), "", JS_EVAL_TYPE_MODULE));
                JS_SetModuleLoaderFunc (runtime, nullptr, nullptr, nullptr);

                if (handleResult)
                    handleResult ({}, result.toChocValue());
            }
            else
            {
                auto result = takeValue (JS_Eval (context, code.c_str(), code.size(), "", JS_EVAL_TYPE_GLOBAL));

                if (handleResult)
                    handleResult ({}, result.toChocValue());
            }
        }
        catch (const std::exception& e)
        {
            if (handleResult)
                handleResult (e.what(), {});
        }
    }

    static JSModuleDef* moduleLoaderFunc (JSContext* ctx, const char* moduleName, void* resolveModule)
    {
        auto content = (*static_cast<Context::ReadModuleContentFn*> (resolveModule)) (std::string_view (moduleName));

        if (! content)
            throw Error ("Cannot find module '" + std::string (moduleName) + "'");

        auto result = ValuePtr (JS_Eval (ctx, content->data(), content->length(), moduleName,
                                         JS_EVAL_TYPE_MODULE | JS_EVAL_FLAG_COMPILE_ONLY), ctx);
        result.throwIfError();
        return static_cast<JSModuleDef*> (JS_VALUE_GET_PTR (result.get()));
    }

    void prepareForCall (std::string_view functionName, uint32_t) override
    {
        functionToCall = JS_NewAtomLen (context, functionName.data(), functionName.length());
    }

    choc::value::Value performCall() override
    {
        auto global = takeValue (JS_GetGlobalObject (context));
        auto returnVal = takeValue (JS_Invoke (context, global.get(), functionToCall, static_cast<int> (functionArgs.size()), functionArgs.data()));
        JS_FreeAtom (context, functionToCall);

        for (auto arg : functionArgs)
            JS_FreeValue (context, arg);

        functionArgs.clear();
        return returnVal.toChocValue();
    }

    //==============================================================================
    struct ValuePtr
    {
        ValuePtr() = delete;
        ValuePtr (const ValuePtr&) = delete;
        ValuePtr& operator= (const ValuePtr&) = delete;
        ValuePtr (JSValue v, JSContext* c) : value (v), context (c) {}
        ValuePtr (ValuePtr&& other) : value (other.value), context (other.context) { other.context = {}; }
        ValuePtr& operator= (ValuePtr&& other) { reset(); value = other.value; context = other.context; other.context = {}; return *this; }
        ~ValuePtr() { reset(); }

        void reset()                { if (context) JS_FreeValue (context, value); }
        JSValue release()           { auto v = value; context = {}; return v; }
        JSValue get() const         { return value; }

        std::string toString() const
        {
            CHOC_ASSERT (context != nullptr);

            if (JS_IsUndefined (value))
                return {};

            size_t len = 0;
            auto c = JS_ToCStringLen2 (context, &len, value, false);
            std::string s (c, len);
            JS_FreeCString (context, c);
            return s;
        }

        ValuePtr operator[] (uint32_t index) const    { return takeValue (JS_GetPropertyUint32 (context, value, index)); }
        ValuePtr operator[] (const char* name) const  { return takeValue (JS_GetPropertyStr (context, value, name)); }

        choc::value::Value toChocValue() const
        {
            CHOC_ASSERT (context != nullptr);

            if (JS_IsNumber (value))
            {
                double d = 0;
                JS_ToFloat64 (context, std::addressof (d), value);
                return choc::value::createFloat64 (d);
            }

            if (JS_IsBool (value))
                return choc::value::createBool (JS_ToBool (context, value) != 0);

            if (JS_IsString (value))
            {
                size_t len = 0;
                auto s = JS_ToCStringLen2 (context, std::addressof (len), value, false);
                auto val = choc::value::createString (std::string_view (s, len));
                JS_FreeCString (context, s);
                return val;
            }

            if (JS_IsArray (context, value))
            {
                auto lengthProp = (*this)["length"];
                uint32_t len = 0;
                JS_ToUint32 (context, &len, lengthProp.get());

                return choc::value::createArray (len, [this] (uint32_t i)
                {
                    return (*this)[i].toChocValue();
                });
            }

            if (JS_IsObject (value))
            {
                std::vector<std::string> propNames;
                bool hasClassName = false;

                for (auto obj = takeValue (JS_DupValue (context, value));;)
                {
                    JSPropertyEnum* properties = nullptr;
                    uint32_t numProps = 0;

                    if (JS_GetOwnPropertyNames (context, &properties, &numProps, obj.get(), JS_GPN_STRING_MASK | JS_GPN_ENUM_ONLY) != 0)
                        return choc::value::createObject ({});

                    propNames.reserve (numProps);

                    for (uint32_t i = 0; i < numProps; ++i)
                    {
                        auto name = JS_AtomToCString (context, properties[i].atom);
                        auto nameString = std::string (name);

                        if (nameString == objectNameAttribute)
                            hasClassName = true;
                        else
                            propNames.push_back (std::move (nameString));

                        JS_FreeCString (context, name);
                        JS_FreeAtom (context, properties[i].atom);
                    }

                    if (properties != nullptr)
                        js_free (context, properties);

                    auto proto = takeValue (JS_GetPrototype (context, obj.get()));

                    if (! JS_IsObject (proto.get()))
                        break;

                    obj = std::move (proto);
                }

                auto o = choc::value::createObject (hasClassName ? (*this)[objectNameAttribute].toChocValue().toString()
                                                                 : std::string());

                for (auto& propName : propNames)
                    o.setMember (propName, (*this)[propName.c_str()].toChocValue());

                return o;
            }

            throwIfError();
            return {};
        }

        void throwIfError() const
        {
            if (JS_IsError (context, value))
                throw Error (toString());

            if (JS_IsException (value))
            {
                auto exception = takeValue (JS_GetException (context));
                throw Error (exception["message"].toString() + "\n" + exception["stack"].toString());
            }
        }

        ValuePtr takeValue (JSValue v) const   { return ValuePtr (v, context); }

        JSValue value;
        JSContext* context;
    };

    //==============================================================================
    ValuePtr takeValue (JSValue v) const    { return ValuePtr (v, context); }

    static JSValue callbackFunction (JSContext* c, JSValueConst, int argc, JSValueConst* args, int magic)
    {
        auto context = static_cast<QuickJSContext*> (JS_GetContextOpaque (c));
        return context->invokeCallback (static_cast<uint32_t> (magic), args, argc);
    }

    JSValue invokeCallback (uint32_t magic, JSValueConst* args, int numArgs)
    {
        std::vector<choc::value::Value> argList;
        argList.reserve (static_cast<uint32_t> (numArgs));

        for (int i = 0; i < numArgs; ++i)
        {
            auto arg = takeValue (args[i]);
            argList.push_back (arg.toChocValue());
            arg.release();
        }

        auto& fn = registeredFunctions[magic];
        return valueToJS (fn (ArgumentList { argList.data(), argList.size() })).release();
    }

    void registerFunction (const std::string& name, Context::NativeFunction fn) override
    {
        auto global = JS_GetGlobalObject (context);
        auto f = JS_NewCFunctionMagic (context, callbackFunction, name.c_str(), 0, JS_CFUNC_generic_magic, static_cast<int> (registeredFunctions.size()));
        JS_SetPropertyStr (context, global, name.c_str(), f);
        JS_FreeValue (context, global);

        registeredFunctions.push_back (std::move (fn));
    }

    ValuePtr stringToJS (std::string_view s)
    {
        return takeValue (JS_NewStringLen (context, s.data(), s.length()));
    }

    ValuePtr valueToJS (const choc::value::ValueView& v)
    {
        if (v.isInt32())    return takeValue (JS_NewInt32   (context, v.get<int32_t>()));
        if (v.isInt64())    return takeValue (JS_NewInt64   (context, v.get<int64_t>()));
        if (v.isFloat())    return takeValue (JS_NewFloat64 (context, v.get<double>()));
        if (v.isBool())     return takeValue (JS_NewBool    (context, v.getBool()));
        if (v.isString())   return stringToJS (v.getString());

        if (v.isArray() || v.isVector())
        {
            auto a = takeValue (JS_NewArray (context));
            uint32_t index = 0;

            for (auto element : v)
                JS_SetPropertyUint32 (context, a.get(), index++, valueToJS (element).release());

            return a;
        }

        if (v.isObject())
        {
            auto o = takeValue (JS_NewObject (context));

            if (auto className = v.getObjectClassName(); ! className.empty())
                JS_SetPropertyStr (context, o.get(), objectNameAttribute, stringToJS (className).release());

            v.visitObjectMembers ([&] (std::string_view name, const choc::value::ValueView& value)
            {
                JS_SetPropertyStr (context, o.get(), std::string (name).c_str(), valueToJS (value).release());
            });

            return o;
        }

        CHOC_ASSERT (v.isVoid());
        return takeValue (JS_UNDEFINED);
    }

    //==============================================================================
    JSRuntime* runtime = {};
    JSContext* context = {};
    std::vector<Context::NativeFunction> registeredFunctions;
    std::vector<JSValue> functionArgs;
    JSAtom functionToCall = {};

    static constexpr const char* objectNameAttribute = "_objectName";
};

